package com.example.expensemanager.ui.main;

import android.app.Activity;
import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.widget.Button;
import android.widget.Toast;

import androidx.activity.result.ActivityResultLauncher;
import androidx.activity.result.contract.ActivityResultContracts;
import androidx.appcompat.app.AlertDialog;
import androidx.appcompat.app.AppCompatActivity;
import androidx.lifecycle.ViewModelProvider;

import com.example.expensemanager.R;
import com.example.expensemanager.data.local.TransactionEntity;
import com.example.expensemanager.utils.BackupManager;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;

import java.util.List;

public class MainActivity extends AppCompatActivity {

    private TransactionViewModel viewModel;
    private BackupManager backupManager;
    private Button btnExportData, btnImportData;

    private ActivityResultLauncher<Intent> exportLauncher;
    private ActivityResultLauncher<Intent> importLauncher;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        viewModel = new ViewModelProvider(this).get(TransactionViewModel.class);
        backupManager = new BackupManager(this);

        initViews();
        setupExportImportLaunchers();
        setupButtons();

        // ...existing code...
    }

    private void initViews() {
        btnExportData = findViewById(R.id.btnExportData);
        btnImportData = findViewById(R.id.btnImportData);
        // ...existing code...
    }

    private void setupExportImportLaunchers() {
        // Export launcher
        exportLauncher = registerForActivityResult(
                new ActivityResultContracts.StartActivityForResult(),
                result -> {
                    if (result.getResultCode() == Activity.RESULT_OK && result.getData() != null) {
                        Uri uri = result.getData().getData();
                        if (uri != null) {
                            exportDataToFile(uri);
                        }
                    }
                }
        );

        // Import launcher
        importLauncher = registerForActivityResult(
                new ActivityResultContracts.StartActivityForResult(),
                result -> {
                    if (result.getResultCode() == Activity.RESULT_OK && result.getData() != null) {
                        Uri uri = result.getData().getData();
                        if (uri != null) {
                            importDataFromFile(uri);
                        }
                    }
                }
        );
    }

    private void setupButtons() {
        btnExportData.setOnClickListener(v -> exportData());
        btnImportData.setOnClickListener(v -> importData());
        // ...existing code...
    }

    private void exportData() {
        FirebaseUser user = FirebaseAuth.getInstance().getCurrentUser();
        if (user == null) {
            Toast.makeText(this, "Please login first", Toast.LENGTH_SHORT).show();
            return;
        }

        // Get all transactions for current user
        viewModel.getAllTransactions().observe(this, transactions -> {
            if (transactions == null || transactions.isEmpty()) {
                Toast.makeText(this, "No transactions to export", Toast.LENGTH_SHORT).show();
                return;
            }

            // Create file picker intent
            Intent intent = new Intent(Intent.ACTION_CREATE_DOCUMENT);
            intent.addCategory(Intent.CATEGORY_OPENABLE);
            intent.setType("application/json");
            intent.putExtra(Intent.EXTRA_TITLE, backupManager.generateBackupFilename(user.getUid()));
            exportLauncher.launch(intent);
        });
    }

    private void exportDataToFile(Uri uri) {
        new Thread(() -> {
            List<TransactionEntity> transactions = viewModel.getAllTransactionsSync();

            if (transactions == null || transactions.isEmpty()) {
                runOnUiThread(() -> 
                    Toast.makeText(this, "No transactions to export", Toast.LENGTH_SHORT).show()
                );
                return;
            }

            String jsonData = backupManager.exportToJson(transactions);

            if (jsonData != null && backupManager.writeToFile(uri, jsonData)) {
                runOnUiThread(() -> 
                    Toast.makeText(this, "Exported " + transactions.size() + " transactions successfully", Toast.LENGTH_LONG).show()
                );
            } else {
                runOnUiThread(() -> 
                    Toast.makeText(this, "Export failed", Toast.LENGTH_SHORT).show()
                );
            }
        }).start();
    }

    private void importData() {
        FirebaseUser user = FirebaseAuth.getInstance().getCurrentUser();
        if (user == null) {
            Toast.makeText(this, "Please login first", Toast.LENGTH_SHORT).show();
            return;
        }

        // Show confirmation dialog
        new AlertDialog.Builder(this)
                .setTitle("Import Backup")
                .setMessage("This will add transactions from the backup file. Existing transactions will not be deleted. Continue?")
                .setPositiveButton("Import", (dialog, which) -> {
                    Intent intent = new Intent(Intent.ACTION_OPEN_DOCUMENT);
                    intent.addCategory(Intent.CATEGORY_OPENABLE);
                    intent.setType("application/json");
                    importLauncher.launch(intent);
                })
                .setNegativeButton("Cancel", null)
                .show();
    }

    private void importDataFromFile(Uri uri) {
        new Thread(() -> {
            String jsonData = backupManager.readFromFile(uri);

            if (jsonData == null) {
                runOnUiThread(() -> 
                    Toast.makeText(this, "Failed to read backup file", Toast.LENGTH_SHORT).show()
                );
                return;
            }

            List<TransactionEntity> transactions = backupManager.importFromJson(jsonData);

            if (transactions == null || transactions.isEmpty()) {
                runOnUiThread(() -> 
                    Toast.makeText(this, "No valid transactions found in backup", Toast.LENGTH_SHORT).show()
                );
                return;
            }

            // Get current user ID
            FirebaseUser user = FirebaseAuth.getInstance().getCurrentUser();
            if (user == null) {
                runOnUiThread(() -> 
                    Toast.makeText(this, "User not logged in", Toast.LENGTH_SHORT).show()
                );
                return;
            }

            // Update userId and insert each transaction
            int importedCount = 0;
            for (TransactionEntity transaction : transactions) {
                transaction.id = 0; // Reset ID to auto-generate new ones
                transaction.userId = user.getUid(); // Set current user ID
                viewModel.insertSync(transaction);
                importedCount++;
            }

            final int count = importedCount;
            runOnUiThread(() -> {
                Toast.makeText(this, "Imported " + count + " transactions successfully", Toast.LENGTH_LONG).show();
                viewModel.refreshUser(); // Refresh the transaction list
            });
        }).start();
    }

    // ...existing code...
}

// ...existing code...

public class TransactionViewModel extends AndroidViewModel {

    // ...existing code...

    public void insert(TransactionEntity entity) {
        // Set userId before inserting
        FirebaseUser user = FirebaseAuth.getInstance().getCurrentUser();
        if (user != null) {
            entity.userId = user.getUid();
        }
        repository.insert(entity);
    }

    public void insertSync(TransactionEntity entity) {
        // Synchronous insert for bulk import
        repository.insertSync(entity);
    }

    public List<TransactionEntity> getAllTransactionsSync() {
        // Synchronous method to get all transactions
        FirebaseUser user = FirebaseAuth.getInstance().getCurrentUser();
        if (user != null) {
            return repository.getTransactionsByUserSync(user.getUid());
        }
        return new ArrayList<>();
    }

    // ...existing code...
}

package com.example.expensemanager.utils;

import android.content.Context;
import android.net.Uri;
import android.util.Log;

import com.example.expensemanager.data.local.TransactionEntity;
import com.google.gson.Gson;
import com.google.gson.reflect.TypeToken;

import java.io.BufferedReader;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.lang.reflect.Type;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.List;
import java.util.Locale;

public class BackupManager {

    private static final String TAG = "BackupManager";
    private final Context context;
    private final Gson gson;

    public BackupManager(Context context) {
        this.context = context;
        this.gson = new Gson();
    }

    /**
     * Export transactions to JSON format
     */
    public String exportToJson(List<TransactionEntity> transactions) {
        try {
            BackupData backupData = new BackupData();
            backupData.transactions = transactions;
            backupData.exportDate = System.currentTimeMillis();
            backupData.version = "1.0";

            return gson.toJson(backupData);
        } catch (Exception e) {
            Log.e(TAG, "Error exporting to JSON", e);
            return null;
        }
    }

    /**
     * Write JSON data to output stream (file)
     */
    public boolean writeToFile(Uri uri, String jsonData) {
        try {
            OutputStream outputStream = context.getContentResolver().openOutputStream(uri);
            if (outputStream != null) {
                outputStream.write(jsonData.getBytes());
                outputStream.close();
                return true;
            }
        } catch (Exception e) {
            Log.e(TAG, "Error writing to file", e);
        }
        return false;
    }

    /**
     * Read JSON data from input stream (file)
     */
    public String readFromFile(Uri uri) {
        try {
            InputStream inputStream = context.getContentResolver().openInputStream(uri);
            if (inputStream != null) {
                BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));
                StringBuilder stringBuilder = new StringBuilder();
                String line;
                while ((line = reader.readLine()) != null) {
                    stringBuilder.append(line);
                }
                inputStream.close();
                return stringBuilder.toString();
            }
        } catch (Exception e) {
            Log.e(TAG, "Error reading from file", e);
        }
        return null;
    }

    /**
     * Import transactions from JSON format
     */
    public List<TransactionEntity> importFromJson(String jsonData) {
        try {
            Type backupType = new TypeToken<BackupData>(){}.getType();
            BackupData backupData = gson.fromJson(jsonData, backupType);

            if (backupData != null && backupData.transactions != null) {
                Log.d(TAG, "Imported " + backupData.transactions.size() + " transactions");
                return backupData.transactions;
            }
        } catch (Exception e) {
            Log.e(TAG, "Error importing from JSON", e);
        }
        return null;
    }

    /**
     * Generate backup filename with timestamp
     */
    public String generateBackupFilename(String userId) {
        SimpleDateFormat sdf = new SimpleDateFormat("yyyyMMdd_HHmmss", Locale.getDefault());
        String timestamp = sdf.format(new Date());
        return "expense_backup_" + userId.substring(0, Math.min(8, userId.length())) + "_" + timestamp + ".json";
    }

    /**
     * Data class for backup structure
     */
    public static class BackupData {
        public List<TransactionEntity> transactions;
        public long exportDate;
        public String version;
    }
}

