                    <TrendingUp className="w-6 h-6 text-green-600" />
                  ) : (
                    <TrendingDown className="w-6 h-6 text-red-600" />
                  )}
                </div>
                <div>
                  <h3 className="font-semibold text-gray-900">
                    {/* Display "Income" for income, category name for expense */}
                    {transaction.title || (transaction.type === 'income' ? 'Income' : transaction.category)}
                  </h3>
                  <p className="text-sm text-gray-500">
                    {transaction.paymentMethod} • {transaction.date}
                  </p>
                  {transaction.description && (
                    <p className="text-sm text-gray-600 mt-1">
                      {transaction.description}
                    </p>
                  )}
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <span
                  className={`text-lg font-bold ${
                    transaction.type === 'income'
                      ? 'text-green-600'
                      : 'text-red-600'
                  }`}
                >
                  {/* Display amount with ৳ symbol */}
                  {transaction.type === 'income' ? '+' : '-'}৳{transaction.amount.toFixed(2)}
                </span>
                <button
                  onClick={() => onDeleteTransaction(transaction.id)}
                  className="text-gray-400 hover:text-red-600 transition-colors"
                >
                  <Trash2 className="w-5 h-5" />
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* ...existing code... */}
    </div>
  );
}

export interface Transaction {
  id: string;
  amount: number;
  category: string | null; // Allow null for income transactions
  paymentMethod: string;
  type: 'income' | 'expense';
  date: string;
  description: string;
  title?: string; // Add optional title field
}

